mapper.insert:
insert into user_task(task_id, user_id, `level`, trigger_value, status)
values (#{userTask.taskId}, #{userTask.userId}, #{userTask.level}, #{userTask.triggerValue},
        #{userTask.status}) ON DUPLICATE KEY
UPDATE level = #{userTask.level}
    ,trigger_value = #{userTask.triggerValue}
    ,status = #{userTask.status}
